jQuery(document).ready(function($){"use strict";$(window).on('load',function(){setTimeout(()=>{$('#preloader').addClass('loaded')},1000)})
$(window).on('scroll',function(){if($(window).scrollTop()>250){$('.navbar-fixed').addClass('sticky fade_down_effect')}else{$('.navbar-fixed').removeClass('sticky fade_down_effect')}});if($('.popup-btn').length>0){$('.popup-btn').magnificPopup({disableOn:500,type:'iframe',mainClass:'mfp-fade',removalDelay:160,preloader:!1,fixedContentPos:!1})}
$('.dropdown > a').on('click',function(e){e.preventDefault();location.href=this.href;var dropdown=$(this).parent('.dropdown');dropdown.find('>.dropdown-menu').slideToggle('show');$(this).toggleClass('opened');return!1});$(".search-close").on("click",function(){$(".cart-link i.icon-search").addClass("show");$(".cart-link form .search-box").removeClass("show");$(".cart-link i.icon-cross").removeClass("show");$(".cart-link .cross-icon").removeClass("show")});$(".header-search-icon").on("click",function(){$(".cart-link i.icon-search").removeClass("show");$(".cart-link form .search-box").addClass("show");$(".cart-link i.icon-cross").addClass("show");$(".cart-link .cross-icon").addClass("show")});$(window).scroll(function(){if($(this).scrollTop()>50){$('#back-to-top').fadeIn()}else{$('#back-to-top').fadeOut()}});$('#back-to-top').on('click',function(){$('#back-to-top').tooltip('hide');$('body,html').animate({scrollTop:0},800);return!1});$('#back-to-top').tooltip('hide');$('.contactMe select').select2({placeholder:'Select Me',theme:"flat",placeholder:'Select an option...',minimumResultsForSearch:-1})})